import styled from 'styled-components';
import { SunIcon } from '@heroicons/react/solid';
import { MoonIcon } from '@heroicons/react/solid';
import { t } from 'i18next';
import './components/SettingsPanel/style.css';

export const Toggle = ({ theme, toggleTheme }) => {
    const isLight = theme === 'light';

    return (
      <ToggleContainer lightTheme={isLight} onClick={toggleTheme}>
        <button class="dark_off" href="#" role="menuitem" tabindex="-1" alt="Settings">
          {t('dark_theme.off')}
            <SunIcon className="sun-icon"/>
            </button>

        <button class="dark_on" href="#" role="menuitem" tabindex="-1" alt="Settings">
          {t('dark_theme.on')}
            <MoonIcon className="moon-icon"/>
            </button>
      </ToggleContainer>
    );
  };

const ToggleContainer = styled.button`
  background: ${({ theme }) => theme.gradient};
  border: 2px solid ${({ theme }) => theme.toggleBorder};
  border-radius: 30px;
  cursor: pointer;
  display: flex;
  font-size: 0.5rem;
  justify-content: space-between;
  margin: 0 auto;
  overflow: hidden;
  padding: 0.5rem;
  position: relative;
  width: 8rem;
  height: 4rem;

  svg {
    height: auto;
    width: 2.5rem;
    transition: all 0.3s linear;

    // sun icon
    &:first-child {
      display: ${({ lightTheme }) => lightTheme ? 'none' : 'block' };
    }

    // moon icon
    &:nth-child(2) {
      display: ${({ lightTheme }) => lightTheme ? 'none' : 'block' };
    }

    // sun icon
    &:nth-child(3) {
      display: ${({ lightTheme }) => lightTheme ? 'block' : 'none' };
    }

    // moon icon
    &:nth-child(4) {
      display: ${({ lightTheme }) => lightTheme ? 'block' : 'none' };
    }
  }
`;
