import Logo from './components/Logo/index';
import Header from './components/Header/index';
import SearchBar from './components/SearchBar/index';
import SearchButton from './components/SearchButton/index';
import PageLanguage from './components/PageLanguage/index';
import Footer from './components/Footer/index';
import { useTranslation } from 'react-i18next';
import './App.css';
import { lightTheme } from './components/Theme/light';
import { darkTheme } from './components/Theme/dark';
import { ThemeProvider } from 'styled-components';
import { GlobalStyles } from './components/Theme/global';

function App() {
  const { t } = useTranslation()

  function validateForm() {
    var x = document.forms["myForm"]["fname"].value;
    if (x === "") {
      alert("Name must be filled out");
      return false;
    }
  }

  return ( <>
        <ThemeProvider theme={'light' ? lightTheme : darkTheme}>
          <GlobalStyles />
    <Header />
      <main>
        <Logo />
        <div className="form-container">
          <form name="search" onSubmit="return validateForm()" method="get" action="https://www.google.com/search" required>
            <SearchBar />
            <SearchButton />
          </form>
        </div>
        <PageLanguage />
      </main>
    <Footer />
    </ThemeProvider>
  </> );
}

export default App
