import { useForm } from 'react-hook-form';

export default function Register() {
  const { register, handleSubmit, formState: { errors } } = useForm({
    defaultValues: {
      firstName: '',
      lastName: ''
    }
  });

  const onSubmit = data => console.log(data);



  return (
    <>
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className="name-row">
      <input 
        {...register("firstName", {
        required: true,
        minLength: 2,
        maxLength: 16,
        pattern: /^[a-z,A-Z ,.'-]{2,16}$/,
        })}
        name="firstName"
        type="text"
      />
      <input 
        {...register("lastName", {
        required: true,
        minLength: 2,
        maxLength: 16,
        pattern: /^[a-z,A-Z ,.'-]{2,16}$/,
        validate: (value) => {
          return !!value.trim()
        }
        })}
        name="lastName"
        type="text"
      />
      </div>
      {errors.firstName?.type === "required" && <span>Enter first name</span>}
      {errors.firstName?.type === "pattern" && <span>Invalid first name</span>}
      {errors.firstName?.type === "minLength" && <span>First name too short</span>}
      {errors.firstName?.type === "maxLength" && <span>First name too long</span>}


      {errors.lastName?.type === "required" && <span>Enter last name</span>}
      {errors.lastName?.type === "pattern" && <span>Invalid last name</span>}
      {errors.lastName?.type === "minLength" && <span>First name too short</span>}
      {errors.lastName?.type === "maxLength" && <span>First name too long</span>}


      <input type="submit"/>
    </form>
    </>
  )
}
