import React, { useState, useEffect, useRef } from 'react';
import './style.css';
import SettingsPanel from './../../components/SettingsPanel/index';
import { t } from 'i18next';

function useComponentVisible(initialIsVisible) {
    const [isComponentVisible, setIsComponentVisible] = useState(
      initialIsVisible
    );
    const ref = useRef(null);

    const handleHideDropdown = (event: KeyboardEvent) => {
      if (event.key === "Escape") {
        setIsComponentVisible(false);
      }
    };

    const handleClickOutside = event => {
      if (ref.current && !ref.current.contains(event.target)) {
        setIsComponentVisible(false);
      }
    };

    useEffect(() => {
      document.addEventListener("keydown", handleHideDropdown, true);
      document.addEventListener("click", handleClickOutside, true);
      return () => {
        document.removeEventListener("keydown", handleHideDropdown, true);
        document.removeEventListener("click", handleClickOutside, true);
      };
    });

    return { ref, isComponentVisible, setIsComponentVisible };
}

function Footer() {
  return (
    <footer>
        <FooterCountry />
        <div class="footer-content">
            <ul>
                <About />
                <Advertising />
                <Business />
                <HowSearchWorks />
            </ul>
            <ul>
                <Privacy />
                <Terms />
                <Settings />
                const {
    ref,
    isComponentVisible,
    setIsComponentVisible
  } = useComponentVisible(true);
  return (
    <div ref={ref}>
      {isComponentVisible && (
        <>
        <span style={{ border: "1px solid black", padding: "15px" }}>Settings</span>

        <button onClick={() => setIsComponentVisible(false)}>
          Settings
        </button>
        </>
      )}
      {!isComponentVisible && (
        <button onClick={() => setIsComponentVisible(true)}>
          Settings
        </button>
      )}
    </div>
  );
                    <SettingsPanel />
            </ul>
        </div>
    </footer>
  )
}

const FooterCountry = () => (
    <div class="footer-country">
        <p>Argentina</p>
    </div>
);

const About = () => (
    <li>
        <a href="https://about.google/" alt="About">
            {t('about')}
        </a>
    </li>
);

const Advertising = () => (
    <li>
        <a href="https://ads.google.com/" alt="Advertising">
            {t('advertising')}
        </a>
    </li>
);

const Business = () => (
    <li>
        <a href="https://smallbusiness.withgoogle.com/" alt="Business">
            {t('business')}
        </a>
    </li>
);

const HowSearchWorks = () => (
    <li>
        <a href="https://www.google.com/search/howsearchworks/" alt="How Search Works">
            {t('how_search_works')}
        </a>
    </li>
);

const Privacy = () => (
    <li>
        <a href="https://policies.google.com/privacy/" alt="Privacy">
            {t('privacy')}
        </a>
    </li>
);

const Terms = () => (
    <li>
        <a href="https://policies.google.com/terms/" alt="Terms">
            {t('terms')}
        </a>
    </li>
);

const Settings = () => (
    <li>
        <a href="#" alt="Settings">
            {t('settings')}
        </a>
    </li>
);

export default Footer
