import { useState } from 'react';
import OutsideClickHandler from 'react-outside-click-handler';
import './style.css';
import GoogleApps from './../../components/GoogleApps/index';

export function ToggleApps(props) {
  const [show, setShow] = useState(false)
  const [isActive, setActive] = useState(false);

  const toggleClass = () => {
    setActive(!isActive);
  };

    return ( <>
      <li className="pl">
        <OutsideClickHandler onOutsideClick={() => {setShow(false);}}>
          <div className={isActive ? 'gapps-icon-link': null} onClick={toggleClass}>
            <button onClick={() => setShow(!show)} alt="Google apps">
                <GApps/>
            </button>
          </div>
          {
          show? <GoogleApps/> : null
          }
        </OutsideClickHandler>
      </li>
    </> )
}

const GApps = () => (
  <div className={"gapps-icon-link" ? "null" : "active"} alt="Google apps" data-title="Google apps">
