import React, { useState } from "react";
import onClickOutside from "react-onclickoutside";
import './style.css';
import SettingsPanel from './../../components/SettingsPanel/index';
import { t } from 'i18next';

function Footer() {
  return (
    <footer>
        <FooterCountry />
        <div class="footer-content">
            <ul>
                <About />
                <Advertising />
                <Business />
                <HowSearchWorks />
            </ul>
            <ul>
                <Privacy />
                <Terms />
                <Settings />
                    <SettingsPanel />
            </ul>
        </div>
    </footer>
  )
}

function DropDown() {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedValue, setSelectedValue] = useState('');
    const toggle = () => setIsOpen(!isOpen);

    DropDown.handleClickOutside = () => setIsOpen(false);

    const selectOption = (value) => {
        setSelectedValue(value);
        setIsOpen(false);
    }

    const optionData = [{
        id: 1,
        text: 'Profile'
    }, {
        id: 2,
        text: 'Timeline'
    }, {
        id: 3,
        text: 'Settings'
    }, {
        id: 4,
        text: 'Log out'
    }]


    return (
        <div className="dd-wrapper">
            <div onClick={toggle} className="dd-selected">
                {selectedValue ? 'Selected: ' + selectedValue : 'Select Option'}
            </div>
            {
                isOpen ?
                    <ul className="dd-items-wrapper">
                        {optionData.map((option) =>
                            <li className={option.text === selectedValue ? 'dd-item active' : 'dd-item'} key={option.id} onClick={() => selectOption(option.text)}>
                                <div>{option.text}</div>
                            </li>
                        )}
                    </ul>
                    : null
            }

        </div>
    );
};

const FooterCountry = () => (
    <div class="footer-country">
        <p>Argentina</p>
    </div>
);

const About = () => (
    <li>
        <a href="https://about.google/" alt="About">
            {t('about')}
        </a>
    </li>
);

const Advertising = () => (
    <li>
        <a href="https://ads.google.com/" alt="Advertising">
            {t('advertising')}
        </a>
    </li>
);

const Business = () => (
    <li>
        <a href="https://smallbusiness.withgoogle.com/" alt="Business">
            {t('business')}
        </a>
    </li>
);

const HowSearchWorks = () => (
    <li>
        <a href="https://www.google.com/search/howsearchworks/" alt="How Search Works">
            {t('how_search_works')}
        </a>
    </li>
);

const Privacy = () => (
    <li>
        <a href="https://policies.google.com/privacy/" alt="Privacy">
            {t('privacy')}
        </a>
    </li>
);

const Terms = () => (
    <li>
        <a href="https://policies.google.com/terms/" alt="Terms">
            {t('terms')}
        </a>
    </li>
);

const Settings = () => (
    <li>
        <a href="#" alt="Settings">
            {t('settings')}
        </a>
    </li>
);

const clickOutsideConfig = {
    handleClickOutside: () => DropDown.handleClickOutside,

};

export default onClickOutside(Footer, DropDown, clickOutsideConfig);
