import axios from 'axios';
import { ShowPassword } from '../../../components/SignPage/ShowPassword';
import { Link } from 'react-router-dom';
import { t } from 'i18next';
import { useContext, useState, useEffect } from 'react';
import { UserContext } from '../../../usercontext';

import './style.css';
import './../../../components/SignPage/Container/style.css';
import './../../../components/SignPage/NextButton/style.css';
import './../../../components/SignPage/Heading/style.css';
import './../../../components/SignPage/SignInForm/style.css';
import './../../../components/SignPage/ShowPassword/style.css';

export default function SignInPassword({ Logo, value, setValue, register, handleSubmit, onSubmit, Next }) {
  const [user, setUser] = useState();

  const response = axios.get;

  useEffect(({ value }) => {
    axios.get("http://localhost:5000/users?email=" + value.email).then((response) => {
      setUser(response.data);
    });
  }, []);


  const firstName = (setUser[0].firstName);
  const email = (setUser[0].email);

  async function matchPassword(e) {
    const response = await axios.get("http://localhost:5000/users?email=" + value.email);
    const userInput = (value.password);

    if (response.data[0].password === userInput) {
      console.log("password matches");
      return true;
    }
  }

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value
    });
  }

  return (
    <div className="form-container">
      <Logo />
      <Heading firstName={firstName} email={email} />
      <Input />
      <ShowPassword />
      <div className="flex-row">
        <Forgot />
        <Next />
      </div>
    </div>
  )
}

const Heading = ({ firstName, email }) => (
  <>
    <center><h1 className="heading">{t("password_heading")}&nbsp;{firstName}</h1></center>
    <center><div className="email-name">email@gmail.com</div></center>
  </>
);

const Input = () => (
  <form className="signin-form pass-form">
    <input className="input-email" type="password" id="password" required autoComplete="off" minLength="1" spellCheck="false" dir="ltr" />
    <span className="input-placeholder">{t("password_form_placeholder")}</span>
  </form>
);

const Forgot = () => (
  <Link to="/signin/challenge/recovery/">
    <button className="create-account">
      {t("password_forgot")}
    </button>
  </Link>
);
