import { t } from 'i18next';
import { useForm } from 'react-hook-form';

import './style.css'

export default function SignPageFooter() {
  const {register, handleSubmit } = useForm({
    defaultValues: {
      language: ''
    },
    mode: 'onSubmit',
    reValidateMode: 'onChange',
    delayError: 1000,
  });

  return (
    <section className="signup-footer" >
      <div className="signup-footer-container">
      <LanguageSelect 
        register={register}
        handleSubmit={handleSubmit}
      />
      <Links />
      </div>
    </section>
  );
}

const LanguageSelect = ({ register, handleSubmit}) => (
  <>
  <button className="signup-footer-language">
    English (United States)
  </button>
  <form onSubmit={handleSubmit(onSubmit)} className="signup-form" noValidate>
  <select 
  {...register("month", {
    required: true
  })}
  className="signup-input focus-blue" 
  name="month"
  type="month"
  size="1"
  onChange={handleChange}
  value={value.month}
>
  <option value="" className="signup-input-placeholder" defaultValue disabled hidden />
  <option value="01">English (United States)</option>
  <option value="02">Spanish</option>
</select>
<span className="signup-input-placeholder">
  ''
</span>
</form>
</>
);

const Links = () => (
  <ul className="signup-footer-links">
    <li>{t('help')}</li>
    <li>{t('privacy')}</li>
    <li>{t('terms')}</li>
  </ul>
);
