import axios from 'axios';
import { Information, Back } from '..';
import { t } from 'i18next';
import { ErrorLogo } from '..';
import { useContext } from 'react';
import { UserContext } from '../../../../usercontext';

export default function Form({ value, setValue, register, handleSubmit, errors, watch, Next }) {
  const { user, setUser } = useContext(UserContext);

  const currentUser = async () => {
    let URL = "http://localhost:5000/users?email=" + value.email;
    let response = await axios.get(URL);
    return response.data;
  };

  const onSubmit = async() => {
    try {
      await axios({
        method: "post",
        url: "http://localhost:5000/users",
        data: {
          firstName: value.firstName,
          lastName: value.lastName,
          email: value.email,
          password: value.password,
          gender: value.gender,
          dateOfBirth: value.month + '/' + value.day + '/' + value.year,
        },
        headers: { "Content-Type": "application/json"},
      });
      setTimeout(function () {
        window.location.href = "http://localhost:3000/";
      }, 250);
    } catch(error) {
      console.log(error.response.data)
    }
    const user = await currentUser();
      setUser(user);
  }

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value
    });
  }

  console.log(watch());

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="signup-form" noValidate>
      <div className="birthday-row">
        <Month 
          register={register}
          value={value}
          handleChange={handleChange}
          errors={errors}
        />
        <Day 
          register={register}
          value={value}
          handleChange={handleChange}
          errors={errors}
        />
        <Year 
          register={register}
          value={value}
          handleChange={handleChange}
          errors={errors}
        />
      </div>
      <BirthdayErrors errors={errors}/>
      <Gender 
        register={register}
        value={value}
        handleChange={handleChange}
        errors={errors}
      />
      <GenderErrors errors={errors}/>
      <Information />
      <div className="button-row">
        <Back />
        <Next/>
      </div>
    </form>
  )
}

