import { Information, Back } from '..';
import { t } from 'i18next';
import axios from 'axios';

export default function Form({ value, setValue, register, handleSubmit, errors, watch, Next }) {
  const onSubmit = async() => {
    try {
      const response = await axios({
        method: "post",
        url: "http://localhost:5000/users",
        data: {
          firstName: value.firstName,
          lastName: value.lastName,
          email: value.email,
          password: value.password,
          month: value.month,
          day: value.day,
          year: value.year,
          gender: value.gender,
        },
        headers: { "Content-Type": "application/json"},
      });
      setTimeout(function () {
        window.location.href = "http://localhost:3000/";
      }, 250);
    } catch(error) {
      console.log(error.response.data)
    }
  }

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value
    });
  }

  console.log(watch());

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="signup-form" noValidate>
      <div className="birthday-row">
        <div className="input-container">
          <select 
            {...register("month", {
              required: true
            })}
            className="signup-input" 
            name="month"
            type="month"
            size="1"
            onChange={handleChange}
            value={value.month}
          >
            <option value="" className="signup-input-placeholder" selected disabled hidden />
            <option>January</option>
            <option>February</option>
            <option>March</option>
            <option value={value.month}>April</option>
            <option value={value.month}>May</option>
            <option value={value.month}>June</option>
            <option value={value.month}>July</option>
            <option value={value.month}>August</option>
            <option value={value.month}>September</option>
            <option value={value.month}>October</option>
            <option value={value.month}>November</option>
            <option value={value.month}>December</option>
          </select>
          <span className="signup-input-placeholder">
            ''
          </span>
        </div>
        <div className="input-container">
          <input 
            {...register("day", {
              required: true,
              minLength: 1,
              maxLength: 2,
              pattern: /[0-9]$/,
              validate: (value) => {
                return !!value.trim()
              }
            })}
            autoComplete="off"
            name="day"
            type="number"
            min="0"
            max="32"
            value={value.day}
            onChange={handleChange}
            className="signup-input"
          />
          <span className="signup-input-placeholder">
            Day
          </span>
        </div>
        <div className="input-container">
          <input 
            {...register("year", {
              required: true,
              minLength: 4,
              maxLength: 4,
              pattern: /[0-9]{4}$/,
              validate: (value) => {
                return !!value.trim()
              }
            })}
            autoComplete="off"
            name="year"
            type="number"
            min="1900"
            max="2022"
            value={value.year}
            onChange={handleChange}
            className="signup-input"
          />
          <span className="signup-input-placeholder">
            Year
          </span>
        </div>
      </div>
      <BirthdayNote errors={errors}/>
      <div className="input-container">
        <select 
          {...register("gender", {
            required: true
          })}
          className="signup-input"
          name="gender"
          size="1"
        >
          <option value={value.gender}>Male</option>
          <option value={value.gender}>Female</option>
          <option value={value.gender}>Other</option>
        </select>
        <span className="signup-input-placeholder">
          ''
        </span>
      </div>
      <Information />
      <div className="button-row">
        <Back />
        <Next/>
      </div>
    </form>
  )
}

const BirthdayNote = () => (
  <div className="signup-note">
    Your birthday
  </div>
);
