import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { t } from 'i18next';

import './style.css';
import './../../../components/SignPage/Container/style.css';
import './../../../components/SignPage/NextButton/style.css';
import './../../../components/SignPage/Heading/style.css';
import './../../../components/SignPage/SignInForm/style.css';

export default function SignInEmail({ Logo, value, setValue, register, handleSubmit, errors, watch, onSubmit, Next}) {
  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value
    });
  }

  return (
    <div className="form-container">
      <Logo />
      <Heading />
      <Form handleSubmit={handleSubmit} onSubmit={onSubmit} handleChange={handleChange} register={register} />
      <Note />
      <div className="flex-row">
        <CreateAccount />
        <Next />
      </div>
    </div>
  )
}

const Heading = () => (
  <>
    <center><h1 className="heading">{t('sign_in_heading')}</h1></center>
    <center><h4 className="sub-heading">{t('sign_in_subheading')}</h4></center>
  </>
);

const Form = ({ handleSubmit, onSubmit, handleChange, value, register }) => {
  <form onSubmit={handleSubmit(onSubmit)} className="signin-form email-form" noValidate>

    <input 
            {...register("email", {
              required: true,
              minLength: 2,
              validate: {
                checkEmail: async () => await uniqueEmail(),
              }
            })}
            autoComplete="off"
            name="email"
            type="text"
            value={value.email}
            onChange={handleChange}
            className="input-email"
            aria-invalid={errors.email ? "true" : "false"}
          />
          <span className="input-placeholder">
            {t('sign_in_form_placeholder')}
          </span>
  </form>
};

const Note = () => (
  <div className="note">
    {t('sign_in_note')} 
    <br/>
    <a className="button-blue" href="https://support.google.com/chrome/answer/6130773?hl=en" rel="noreferrer" target="_blank">
      {t('sign_in_learn_more')}
    </a>
  </div>
);

const CreateAccount = () => (
  <Link to="/signup/">
    <button className="create-account">
      {t('sign_in_create_account')}
    </button>
  </Link>
);
