import { Information, Back } from '..';
import { t } from 'i18next';
import axios from 'axios';
import { useContext } from 'react';
import { UserContext } from '../../../../usercontext';

export default function Form({ value, setValue, register, handleSubmit, errors, watch, Next }) {
  const { user, setUser } = useContext(UserContext);

  const currentUser = async () => {
    let URL = "http://localhost:5000/users?email=" + value.email;
    let response = await axios.get(URL);
    return response.data;
  };

  const onSubmit = async() => {
    try {
      await axios({
        method: "post",
        url: "http://localhost:5000/users",
        data: {
          firstName: value.firstName,
          lastName: value.lastName,
          email: value.email,
          password: value.password,
          gender: value.gender,
          dateOfBirth: value.month + '/' + value.day + '/' + value.year,
        },
        headers: { "Content-Type": "application/json"},
      });
      setTimeout(function () {
        window.location.href = "http://localhost:3000/";
      }, 250);
    } catch(error) {
      console.log(error.response.data)
    }
    const user = await currentUser();
      setUser(user);
  }

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value
    });
  }

  console.log(watch());

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="signup-form" noValidate>
      <div className="birthday-row">
        <Month 
          register={register}
          value={value}
          handleChange={handleChange}
        />
        <Day 
          register={register}
          value={value}
          handleChange={handleChange}
          errors={errors}
        />
        <Year 
          register={register}
          value={value}
          handleChange={handleChange}
          errors={errors}
        />
      </div>
      <BirthdayNote errors={errors}/>
      <BirthdayErrors errors={errors}/>
      <Gender 
        register={register}
        value={value}
        handleChange={handleChange}
      />
      <Information />
      <div className="button-row">
        <Back />
        <Next/>
      </div>
    </form>
  )
}

const Month = ({ register, value, handleChange }) => (
  <div className="input-container">
          <select 
            {...register("month", {
              required: true
            })}
            className="signup-input focus-blue" 
            name="month"
            type="month"
            size="1"
            onChange={handleChange}
            value={value.month}
          >
            <option value="" defaultValue disabled hidden />
            <option value="01">{t("months.1")}</option>
            <option value="02">{t("months.2")}</option>
            <option value="03">{t("months.3")}</option>
            <option value="04">{t("months.4")}</option>
            <option value="05">{t("months.5")}</option>
            <option value="06">{t("months.6")}</option>
            <option value="07">{t("months.7")}</option>
            <option value="08">{t("months.8")}</option>
            <option value="09">{t("months.9")}</option>
            <option value="10">{t("months.10")}</option>
            <option value="11">{t("months.11")}</option>
            <option value="12">{t("months.12")}</option>
          </select>
          <div className="signup-input-placeholder-detail">
            {t("month")}
          </div>
        </div>
)

const Day = ({ register, handleChange, value, errors }) => (
  <div className="input-container">
          <input 
            {...register("day", {
              required: true,
              minLength: 1,
              maxLength: 2,
              pattern: /[0-9]$/,
              validate: (value) => {
                return !!value.trim()
              }
            })}
            autoComplete="off"
            name="day"
            type="number"
            min="0"
            max="32"
            value={value.day}
            onChange={handleChange}
            className={ errors.day ? "signup-input-error" : "signup-input" }
          />
          <span className="signup-input-placeholder">
            {t("day")}
          </span>
        </div>
);

const Year = ({ register, value, handleChange, errors }) => (
  <div className="input-container">
          <input 
            {...register("year", {
              required: true,
              minLength: 4,
              maxLength: 4,
              pattern: /[0-9]{4}$/,
              validate: (value) => {
                return !!value.trim()
              }
            })}
            autoComplete="off"
            name="year"
            type="number"
            min="1900"
            max="2022"
            value={value.year}
            onChange={handleChange}
            className={ errors.year ? "signup-input-error" : "signup-input" }
          />
          <span className="signup-input-placeholder">
            {t("year")}
          </span>
        </div>
);

const Gender = ({ register, value, handleChange }) => (
  <div className="input-container">
        <select 
          {...register("gender", {
            required: true
          })}
          className="signup-input input-width focus-blue"
          name="gender"
          onChange={handleChange}
          value={value.gender}
        >
          <option value=""defaultValue disabled hidden/>
          <option value="male">{t("genders.male")}</option>
          <option value="female">{t("genders.female")}</option>
          <option value="other">{t("genders.other")}</option>
        </select>
        <div className="signup-input-placeholder-detail">
          {t("gender")}
        </div>
      </div>
)

const BirthdayNote = () => (
  <div className="signup-note">
    {t("sign-up.detail.your_birthday")}
  </div>
);

const BirthdayErrors = () => (
  <>
  </>
);
