import axios from "axios";

export default function Test() {
  const obj = JSON.parse(localStorage.getItem('user'));
  const email = obj[0].email;
  const firstName = obj[0].firstName;
  const lastName = obj[0].lastName;
  const password = obj[0].password;
  const dateOfBirth = obj[0].dateOfBirth;
  const gender = obj[0].gender;
  const id = obj[0].id;
  const URL = "http://localhost:5000/users?id=" + id;

  const handleSubmit = (data) => {
    axios.delete(URL, {data:{
      firstName: firstName, 
      lastName: lastName, 
      email: email, 
      password: password, 
      gender: gender,
      dateOfBirth: dateOfBirth,
      id: id,
    }, headers:{Authorization: "token"}})
    const response = axios.get(URL)
    return (response.data)
  }

  var user = axios.get("http://localhost:5000/users")

  function onSubmit() {
    // the code you're looking for
    var needle = '3';

    // iterate over each element in the array
    for (var i = 0; i < user.length; i++){
      // look for the entry with a matching `code` value
      if (user[i].id == needle){
        // we found it
        // obj[i].name is the matched result
        console.log(user[i])
      }    
    }
  }

  return (
    <>
      <button onClick={ () => handleSubmit()}>DELETE ACCOUNT</button>
      <form onSubmit={onSubmit}>
        <input
          type="number"
          placeholder="enter number"
        />
      </form>
    </>
  )
}
