import { createGlobalStyle, ThemeProvider } from 'styled-components';

export const GlobalStyles = createGlobalStyle`
  *,
  *::after,
  *::before {
    box-sizing: border-box;
  }

  body {
    background: ${({ theme }) => theme.body};
    color: ${({ theme }) => theme.text};
  }



  small {
  }

  button {
  }

  a {
    color: ${({ theme }) => theme.text};
  }
`;
