import { Information, Back } from '..';
import { useForm } from 'react-hook-form';
import { useState } from 'react';
import { t } from 'i18next';
import Select from 'react-select'
import axios from 'axios';

export default function Form() {
  const [value, setValue] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: ''
  })

  const {register, watch, handleSubmit, formState: { errors }} = useForm({
    defaultValues: {
      firstName: '',
      lastName: '',
      email: '',
      password: '',
      passwordConfirm: ''
    },
    mode: 'onSubmit',
    reValidateMode: 'onChange',
    delayError: 1000,
  });

  const onSubmit = async() => {
    try {
      const response = await axios({
        method: "post",
        url: "http://localhost:5000/users",
        data: {
          firstName: value.firstName,
          lastName: value.lastName,
          email: value.email,
          password: value.password,
        },
        headers: { "Content-Type": "application/json"},
      });
    } catch(error) {
      console.log(error.response.data)
    }
  }

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value
    });
  }

  console.log(errors);

  const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="signup-form" noValidate>
      <div className="birthday-row">
        <div className="input-container">
          <Select options={options} className="signup-input"/>

          <span className="signup-input-placeholder">
            Month
          </span>
        </div>
        <div className="input-container">
          <input 
            {...register("day", {
              required: true,
              minLength: 2,
              maxLength: 2,
              pattern: /^[a-z,A-Z ,.'-]{2,16}$/,
              validate: (value) => {
                return !!value.trim()
              }
            })}
            autoComplete="off"
            name="day"
            type="number"
            value={value.day}
            onChange={handleChange}
            className="signup-input"
          />
          <span className="signup-input-placeholder">
            Day
          </span>
        </div>
        <div className="input-container">
          <input 
            {...register("year", {
              required: true,
              minLength: 4,
              maxLength: 4,
              pattern: /^[a-z,A-Z ,.'-]{2,16}$/,
              validate: (value) => {
                return !!value.trim()
              }
            })}
            autoComplete="off"
            name="year"
            type="number"
            value={value.year}
            onChange={handleChange}
            className="signup-input"
          />
          <span className="signup-input-placeholder">
            Year
          </span>
        </div>
      </div>
      <BirthdayNote errors={errors}/>
      <div className="input-container">
        <input 
          {...register("email", {
            required: true,
          })}
          autoComplete="off"
          name="email"
          type="email"
          value={value.email}
          onChange={handleChange}
          className="signup-input"
        />
        <span className="signup-input-placeholder">
          Gender
        </span>
      </div>
      <Information />
      <div className="button-row">
        <Back />
        <Next/>
      </div>
    </form>
  )
}

const BirthdayNote = ({ errors }) => (
  (!errors.password) ?
    <div className="signup-note">
      Your birthday
    </div> : ''
);

const Next = ({ show, setShow }) => (
  <button type="submit" className="next">
    {t('next')}
  </button>
);
