import React, { Component } from 'react';
import '../SearchBar/style.css';

class Form extends Component {
  constructor (props) {
    super(props);
    this.state = {
      searchInput: '',
      formErrors: {searchInput: ''},
      searchInputValid: false,
      formValid: false
    }
  }

  handleUserInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({[name]: value},
      () => { this.validateField(name, value) });
  }

  validateField(fieldName, value) {
    let fieldValidationErrors = this.state.formErrors;
    let searchInputValid = this.state.searchInputValid;

    switch(fieldName) {
      case 'searchInput':
        searchInputValid = value.length >= 1;
        fieldValidationErrors.searchInput = searchInputValid ? '' : ' is invalid';
        break;
      default:
        break;
    }
    this.setState({formErrors: fieldValidationErrors,
      searchInputValid: searchInputValid,
    }, this.validateForm);
  }

  validateForm() {
    this.setState({formValid: this.state.searchInputValid});
  }

  errorClass(error) {
    return(error.length === 0 ? '' : 'has-error');
  }

  render () {
    return (
      <form className="demoForm">
        <div className={`form-group ${this.errorClass(this.state.formErrors.searchInput)}`}>
          <label htmlFor="searchInput">Search</label>
          <input type="searchInput" required className="form-control" name="searchInput"
            placeholder="Search"
            value={this.state.searchInput}
            onChange={this.handleUserInput}  />
        </div>
        <button type="submit" className="btn btn-primary" disabled={!this.state.formValid}>Sign up</button>
      </form>
    )
  }
}

export default Form;
