import { useRef, useState, useEffect } from "react";
import { t } from 'i18next';

const NAME_REGEX = /^[a-z,A-Z ,.'-]{2,16}$/;
const EMAIL_REGEX =/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;

export default function Register () {
  const userRef = useRef();
  const errRef = useRef();

  const [firstName, setFirstName] = useState('');
  const [validFirstName, setValidFirstName] = useState(false);
  const [firstNameFocus, setFirstNameFocus] = useState(false);

  const [lastName, setLastName] = useState('');
  const [validLastName, setValidLastName] = useState(false);
  const [lastNameFocus, setLastNameFocus] = useState(false);

  const [email, setEmail] = useState('');
  const [validEmail, setValidEmail] = useState(false);
  const [emailFocus, setEmailFocus] = useState(false);

  const [pwd, setPwd] = useState('');
  const [validPwd, setValidPwd] = useState(false);
  const [pwdFocus, setPwdFocus] = useState(false);

  const [matchPwd, setMatchPwd] = useState('');
  const [validMatch, setValidMatch] = useState(false);
  const [matchFocus, setMatchFocus] = useState(false);

  const [errMsg, setErrMsg] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    userRef.current.focus();
  }, [])

  useEffect(() => {
    const result = NAME_REGEX.test(firstName);
    console.log(result);
    console.log(firstName);
    setValidFirstName(result);
  }, [firstName])

  useEffect(() => {
    setValidLastName(NAME_REGEX.test(lastName));
  }, [lastName])

  useEffect(() => {
    setValidLastName(EMAIL_REGEX.test(email));
  }, [email])

  useEffect(() => {
    setValidPwd(PWD_REGEX.test(pwd));
    setValidMatch(pwd === matchPwd);
  }, [pwd, matchPwd])

  useEffect(() => {
    setErrMsg('');
  }, [firstName, lastName, email, pwd, matchPwd])

  return (
    <>
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
      <form className="signup-form">
        <input
          type="text"
          autoComplete="off"
          spellCheck="false"
          dir="ltr"
          ref={userRef}
          value={firstName}
          className="signup-input"
          onChange={(e) => setFirstName(e.target.value)}
          required
          aria-invalid={validFirstName ? "false" : "true"}
          onFocus={() => setFirstNameFocus(true)}
          onBlur={() => setFirstNameFocus(false)}
        />
        <span className="signup-input-placeholder">
          {t("sign_up_first_name")}
        </span>
        <span className={validFirstName || !firstName ? "hide" : "invalid"} >False</span>
      </form>
      <form>
        <input
          type="text"
          autoComplete="off"
          spellCheck="false"
          dir="ltr"
          ref={userRef}
          value={lastName}
          className="signup-input"
          onChange={(e) => setLastName(e.target.value)}
          required
          aria-invalid={validLastName ? "false" : "true"}
          onFocus={() => setLastNameFocus(true)}
          onBlur={() => setLastNameFocus(false)}
        />
        <span className="signup-input-placeholder">
          {t("sign_up_last_name")}
        </span>
        <span className={validLastName || !lastName ? "hide" : "invalid"} >False</span>
      </form>

    </>
  )
}
