import { createGlobalStyle } from 'styled-components';

export const GlobalStyles = createGlobalStyle`
  *,
  *::after,
  *::before {
  }

  body {
    background: ${({ theme }) => theme.body};
    color: ${({ theme }) => theme.text};
  }

  ul.panel {
    background: #fff;
  }

  footer {
  }

  small {
  }

  button {
  }

  a {
    color: ${({ theme }) => theme.text};
  }
`;
