import React from 'react'
import './style.css';
import { t } from 'i18next';

import { SunIcon } from '@heroicons/react/solid';

function SettingsPanel() {
  return (
    <ul class="panel" role="menu">
      <SearchSettings />
      <AdvancedSearch />
      <YourDataInSearch />
      <SearchHistory />
      <SearchHelp />
      <SendFeedback />
      <Separator />
      <DarkTheme />
      <span class="dark toggle"><i class="fas fa-moon d-none" id="dark" title="Switch to Dark Mode"></i></span>          
      <span class="light toggle" id="light"><i class="fas fa-sun" title="Switch to Light Mode"></i></span>   
    </ul>
  )
}

const dark = document.getElementById('dark')
const light = document.getElementById('light')
const catty = document.getElementById('cattyName')
const btn = document.getElementById('btn')

function toggleDark(){
    dark.classList.add('d-none')
    light.classList.remove('d-none')
    btn.classList.add('btn-warning')
    btn.classList.remove('btn-dark')
    document.body.style.backgroundColor = "#212224"
    document.body.style.color = "#fff"
    catty.style.color = "#fff"
}

dark.addEventListener('click', toggleDark)

function toggleLight(){
    light.classList.add('d-none')
    dark.classList.remove('d-none')
    btn.classList.remove('btn-warning')
    btn.classList.add('btn-dark')
    document.body.style.backgroundColor = "#fff"
    document.body.style.color = "#000"
    catty.style.color = "#000"
    btn.style.color = "#fff"
}
light.addEventListener('click', toggleLight)

const SearchSettings = () => (
  <li>
      <a class="panel" href="https://www.google.com/preferences" role="menuitem" tabindex="-1" alt="Search settings">
          {t('search_settings')}
      </a>
  </li>
);

const AdvancedSearch = () => (
  <li>
      <a href="https://www.google.com/advanced_search" role="menuitem" tabindex="-1" alt="Advanced search">
          {t('advanced_search')}
      </a>
  </li>
);

const YourDataInSearch = () => (
  <li>
      <a href="https://myaccount.google.com/yourdata/search?utm_source=googlemenu" role="menuitem" tabindex="-1" alt="Your data in search">
          {t('your_data_in_search')}
      </a>
  </li>
);

const SearchHistory = () => (
  <li>
      <a href="https://www.google.com/history/optout" role="menuitem" tabindex="-1" alt="Search history">
          {t('search_history')}
      </a>
  </li>
);

const SearchHelp = () => (
  <li>
      <a href="https://support.google.com/websearch/#topic=3378866" role="menuitem" tabindex="-1" alt="Search help">
          {t('search_help')}
      </a>
  </li>
);

const SendFeedback = () => (
  <li>
      <a href="https://www.google.com/tools/feedback/" role="menuitem" tabindex="-1" alt="Send feedback">
          {t('send_feedback')}
      </a>
  </li>
);

const Separator = () => (
  <li class="panel_separator" role="separator" />
)

const DarkTheme = () => (
  <li>
      <a class="dark_off" href="#" role="menuitem" tabindex="-1" alt="Settings">
          {t('dark_theme.off')}

          <SunIcon className="sun-icon"/>
      </a>
  </li>
);


export default SettingsPanel
