import { useState } from 'react';
import { SearchIcon, MicrophoneIcon, XIcon } from '@heroicons/react/solid';
import { t } from 'i18next';
import { useForm } from 'react-hook-form';

import './SearchButton/style.css';
import './SearchBar/style.css';

export default function NewForm() {
  const [value, setValue] = useState({
    searchInput: ''
  });

  const {register, handleSubmit } = useForm({
    defaultValues: {
      searchInput: ''
    },
    mode: 'onSubmit',
    reValidateMode: 'onChange',
    delayError: 1000,
  });

  const initialState = '';

  const handleReset = () => {
    setValue(() => initialState)
  }

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value
    });
  }

  return (
    <form onSubmit={handleSubmit} method="get" onReset={handleReset} action="https://www.google.com/search">
      <div className="search-container">
        <div className="search-bar">
          <SearchIcon className="search-icon" />
          <Form
            value={value}
            register={register}
            handleChange={handleChange}
          />
          <ClearButton />
          <VoiceButton />
        </div>
      </div>
      <div className="search-button">
        <ul>
          <li>
            <button className="google-search" name="" type="submit" disabled={value.searchInput == ''}>
              {t('search.google_search')}
            </button>
          </li>
          <LuckySearch />
        </ul>
      </div>
    </form>
  )
}

const Form = ({ register, value, handleChange }) => (
  <input
            {...register("password", {
              required: true,
              minLength: 1,
              validate: (value) => {
                return !!value.trim()
              }
            })}
            type="search"
            title={t('search.searchbar_title')}
            className="searchInput"
            name="q"
            role="search"
            placeholder=" "
            maxLength="2048"
            autoComplete="off"
            spellCheck="false"
            aria-label="Search"
            value={value.searchInput}
            onChange={handleChange}
          />
)

const LuckySearch = () => (
  <li>
    <button className="lucky-search" name="btnI" type="submit">
      {t('search.lucky_search')}
    </button>
  </li>
);

const ClearButton = () => (
  <div className="clear-button">
    <button className="clear tip" type="reset" value='reset' aria-label="clear">
      <XIcon className="x-icon" />
      <span className="clear">{t('search.clear')}</span>
    </button>
  </div>
);

const VoiceButton = () => (
  <a href="https://www.google.com/" type="" className="tip" aria-label="search by voice">
    <MicrophoneIcon className="microphone-icon" />
    <span className="microphone">{t('search.search_by_voice')}</span>
  </a>
);
