import { useRef, useState, useEffect } from "react";
import { t } from 'i18next';

const NAME_REGEX = /^[a-z,A-Z ,.'-]{2,16}$/;
const EMAIL_REGEX =/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;

export default function Register () {
  const errRef = useRef();

  const [firstName, setFirstName] = useState('');
  const [validFirstName, setValidFirstName] = useState(false);
  const [firstNameFocus, setFirstNameFocus] = useState(false);

  const [lastName, setLastName] = useState('');
  const [validLastName, setValidLastName] = useState(false);
  const [lastNameFocus, setLastNameFocus] = useState(false);

  const [email, setEmail] = useState('');
  const [validEmail, setValidEmail] = useState(false);
  const [emailFocus, setEmailFocus] = useState(false);

  const [pwd, setPwd] = useState('');
  const [validPwd, setValidPwd] = useState(false);
  const [pwdFocus, setPwdFocus] = useState(false);

  const [matchPwd, setMatchPwd] = useState('');
  const [validMatch, setValidMatch] = useState(false);
  const [matchFocus, setMatchFocus] = useState(false);

  const [errMsg, setErrMsg] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    const result = NAME_REGEX.test(firstName);
    console.log(result);
    console.log(firstName);
    setValidFirstName(result);
  }, [firstName])

  useEffect(() => {
    const result = NAME_REGEX.test(lastName);
    console.log(result);
    console.log(lastName);
    setValidLastName(result);
  }, [lastName])

  useEffect(() => {
    const result = EMAIL_REGEX.test(email);
    console.log(result);
    console.log(email);
    setValidEmail(result);
  }, [email])

  useEffect(() => {
    const result = PWD_REGEX.test(pwd);
    console.log(result);
    console.log(pwd);
    setValidPwd(result);
    const match = pwd === matchPwd;
    setValidMatch(match);
  }, [pwd, matchPwd])

  useEffect(() => {
    setErrMsg('');
  }, [firstName, lastName, email, pwd, matchPwd])

  const handleSubmit = async (e) => {
    e.preventDefault();
    const v1 = NAME_REGEX.test(firstName, lastName);
    const v2 = EMAIL_REGEX.test(email);
    const v3 = PWD_REGEX.test(pwd, matchPwd);
    if (!v1 || !v2 || !v3) {
      setErrMsg("Invalid Entry");
      return;
    }
    console.log(firstName, lastName, email, pwd);
    setSuccess(true);
  }

  return (
    <>
      { success ? (
        <section>
          <center><h1>Thanks, {firstName}</h1></center>
          <span><b>Email</b></span>
          <p>{email}</p>
          <span><b>Password</b></span>
          <p type="password">{pwd}</p>
          <br/>
          <p>Your Google Account comes with access to everything Google: apps, music, games, and more</p>
          <button>Next</button>
        </section>
      ) : (
      <section className="signpage-container">
        <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
        <section className="form-container">
          <form onSubmit={handleSubmit} className="signup-form">
            <div className="name-row">
              <FirstName
                firstName={firstName}
                setFirstName={setFirstName}
                validFirstName={validFirstName}
                setFirstNameFocus={setFirstNameFocus}
              />
              <LastName
                lastName={lastName}
                setLastName={setLastName}
                validLastName={validLastName}
                setLastNameFocus={setLastNameFocus}
              />
            </div>
            <Email
              email={email}
              setEmail={setEmail}
              validEmail={validEmail}
              setEmailFocus={setEmailFocus}
            />
            <div className="name-row">
              <Password
                pwd={pwd}
                setPwd={setPwd}
                validPwd={validPwd}
                setPwdFocus={setPwdFocus}
              />
              <PasswordConfirm
                matchPwd={matchPwd}
                setMatchPwd={setMatchPwd}
                validMatch={validMatch}
                setMatchFocus={setMatchFocus}
              />
            </div>
            <button>
              Sign Up
            </button>
          </form>
        </section>
      </section>
      )}
    </>
  )
}

const FirstName = ({ firstName, setFirstName, validFirstName, setFirstNameFocus }) => (
  <div className="input-container">
    <input
      id="firstname"
      type="text"
      autoComplete="off"
      spellCheck="false"
      dir="ltr"
      value={firstName}
      onChange={(e) => setFirstName(e.target.value)}
      className="signup-input"
      required
      aria-describedby="firstnamenote"
      aria-invalid={validFirstName ? "false" : "true"}
      onFocus={() => setFirstNameFocus(true)}
      onBlur={() => setFirstNameFocus(false)}
    />
    <span className="signup-input-placeholder">
      {t("sign_up_first_name")}
    </span>
    <div id="firstnamenote" className={firstName && !validFirstName ? "invalid" : "offscreen"} >
      <ErrorLogo/>
      <p className="invalidtext">Enter first name</p>
    </div>
  </div>
)

