import i18n from "i18next";
import { withTranslation, initReactI18next } from "react-i18next";
import translationEN from './locales/en/translationEN.json';
import translationES from './locales/es/translationES.json';

const resources = {
    en: {
        translation: translationEN
    },
    es: {
        translation: translationES
    }
};

const LangComponent = ({ i18n }) => {
    return <div>{i18n.language}</div>;
  };

  const currentLanguage = withTranslation()(LangComponent);

i18n
  .use(initReactI18next)
  .init({
      resources,
      lng: 'en',

      keySeperator: false,

      interpolation: {
          escapeValue: false
      }
  });

  export default i18n;
