    var data = response.data;

    // var data=[{name:"Afghanistan",code:"AF"},{name:"Åland Islands",code:"AX"},{name:"Albania",code:"AL"},{name:"Algeria",code:"DZ"}];
    const userInput = e.target.value;

    // const country = data.find(el => el.id === userInput);
    // => {name: "Albania", code: "AL"}
    console.log(userInput);

    console.log(data)

    const obj = JSON.stringify(data);
    const parse = JSON.parse(obj);
    const country = parse.find(el => el.id === userInput);
    // const firstName = obj[0].firstName;
    // const lastName = obj[0].lastName;
    // const letter = firstName.charAt(0).toUpperCase();
    console.log(country.firstName);
    console.log(parse);
  }

  return (
    <>
      <form onChange={onChange}>
        <input
          type="text"
          name="code"
          placeholder="enter code"
        />

      </form>
    </>
  )
}
