import { t } from 'i18next';
import { useForm } from 'react-hook-form';
import { useState } from 'react';

import './style.css'

export default function SignPageFooter() {
  const {register, handleSubmit } = useForm({
    defaultValues: {
      language: ''
    },
    mode: 'onSubmit',
    reValidateMode: 'onChange',
    delayError: 1000,
  });

  const [value, setValue] = useState({
    language: '',
  })

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value
    });
  }

  return (
    <section className="signup-footer" >
      <div className="signup-footer-container">
      <LanguageSelect 
        register={register}
        handleSubmit={handleSubmit}
        value={value}
        handleChange={handleChange}
      />
      <Links />
      </div>
    </section>
  );
}

const LanguageSelect = ({ register, handleSubmit, handleChange, value }) => (
  <>
  <button className="signup-footer-language">
    English (United States)
  </button>
  <form onSubmit={handleSubmit} noValidate>
  <select 
  {...register("language", {
    required: true
  })}
  name="language"
  size="1"
  onChange={handleChange}
  value={value.language}
>
  <option value="" defaultValue disabled hidden />
  <option value="01">English (United States)</option>
  <option value="02">Spanish</option>
</select>
<span className="signup-input-placeholder">
  ''
</span>
</form>
</>
);

const Links = () => (
  <ul className="signup-footer-links">
    <li>{t('help')}</li>
    <li>{t('privacy')}</li>
    <li>{t('terms')}</li>
  </ul>
);
