import React, { useState } from "react";
import onClickOutside from "react-onclickoutside";
import './style.css';
import { t } from 'i18next';
import SettingsPanel from './../../components/SettingsPanel/index';

function TogglePanel() {
  const [show, setShow] = useState(true)

  TogglePanel.handleClickOutside = () => setShow(false);

    return ( <>
      {
        show?<SettingsPanel />:null
      }
      <button onClick={() => setShow(!show)}><Settings /></button>
    </>)
}

const clickOutsideConfig = {
  handleClickOutside: () => TogglePanel.handleClickOutside,

};

const Settings = () => (
    <li>
        <button class="settings" alt="Settings">
            {t('settings')}
        </button>
    </li>
);

export default TogglePanel(clickOutsideConfig)
