import SignPageFooter from '../../components/SignPage/Footer';
import SignPageHeader from '../../components/SignPage/Header';
import { ShowPassword } from '../../components/SignPage/ShowPassword';
import { Helmet } from 'react-helmet';
import { Link } from 'react-router-dom';
import { t } from 'i18next';
import { useRef, useState, useEffect } from "react";

import './style.css';
import './../../components/SignPage/Container/style.css';
import './../../components/SignPage/NextButton/style.css';
import './../../components/SignPage/Heading/style.css';
import './../../components/SignPage/SignInForm/style.css';
import './../../components/SignPage/ShowPassword/style.css';

const NAME_REGEX = /^[a-zA-ZàáâäãåąčćęèéêëėįìíîïłńòóôöõøùúûüųūÿýżźñçčšžÀÁÂÄÃÅĄĆČĖĘÈÉÊËÌÍÎÏĮŁŃÒÓÔÖÕØÙÚÛÜŲŪŸÝŻŹÑßÇŒÆČŠŽ∂ð ,.'-]{2,16}+$/;
const EMAIL_REGEX =/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;


export default function SignUp(props) {
  const userRef = useRef();
  const errRef = useRef();

  const [firstName, setFirstName] = useState('');
  const [validFirstName, setValidFirstName] = useState(false);
  const [firstNameFocus, setFirstNameFocus] = useState(false);

  const [lastName, setLastName] = useState('');
  const [validLastName, setValidLastName] = useState(false);
  const [lastNameFocus, setLastNameFocus] = useState(false);

  const [email, setEmail] = useState('');
  const [validEmail, setValidEmail] = useState(false);
  const [emailFocus, setEmailFocus] = useState(false);

  const [pwd, setPwd] = useState('');
  const [validPwd, setValidPwd] = useState(false);
  const [pwdFocus, setPwdFocus] = useState(false);

  const [matchPwd, setMatchPwd] = useState('');
  const [validMatch, setValidMatch] = useState(false);
  const [matchFocus, setMatchFocus] = useState(false);

  const [errMsg, setErrMsg] = useState('');
  const [success, setSuccess] = useState(false);

  useEffect(() => {
      userRef.current.focus();
  }, [])

  useEffect(() => {
      setValidFirstName(NAME_REGEX.test(firstName));
  }, [firstName])

  useEffect(() => {
      setValidLastName(NAME_REGEX.test(lastName));
  }, [lastName])

  useEffect(() => {
      setValidLastName(EMAIL_REGEX.test(email));
  }, [email])

  useEffect(() => {
      setValidPwd(PWD_REGEX.test(pwd));
      setValidMatch(pwd === matchPwd);
  }, [pwd, matchPwd])

  useEffect(() => {
      setErrMsg('');
  }, [firstName, lastName, email, pwd, matchPwd])
  return (
    <>
      <Helmet>
        <title>Create your Google Account</title>
      </Helmet>
      <section className="flex-container">
      <SignPageHeader />
      <section className="signpage-container">
        <section className="form-container">
          <Logo />
          <Heading />
            <div className="name-row">
              <FirstName />
              <LastName />
            </div>
            <InputEmail />
            <div className="name-row">
              <Password />
              <ConfirmPassword />
            </div>
            <p className="signup-note">{t("sign_up_password_note")}</p>
            <ShowPassword />
            <section className="flex-row">
              <SignInInstead />
              <Next />
            </section>
          </section>
          <AccountLogo />
