import PageNotFound from './pages/404';
import SignInEmail from './pages/SignIn/Email';
import SignInPassword from './pages/SignIn/Password';
import PasswordRecovery from './pages/PasswordRecovery';
import SignUp from './pages/SignUp';
import Account from './pages/MyAccount/Home';
import Search from './pages/Search';
import PersoalInfo from './pages/MyAccount/PersonalInfo';
import Name from './pages/MyAccount/Name';
import UserContextProvider from './usercontext';
import { darkTheme, lightTheme, GlobalStyles, ThemeProvider, useTheme } from './themes';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';

export default function App() {
  const [theme] = useTheme();

  const PrivateRoute = ({ children}) => {
    let user = JSON.parse(localStorage.getItem('user'));
    if (user !== null) {
      return children
    }
    return <Navigate to="/signin/identifier/" />
  }

  return (
    <>
    <Helmet>
      <title>Google</title>
    </Helmet>
    <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
    <GlobalStyles />
    <UserContextProvider>
      <Router>
        <Routes>
          <Route exact path="/" element={<Search />}/>
          <Route path="/signin/identifier/" element={<SignInEmail />} />
          <Route path="/signin/challenge/" element={<SignInPassword />} />
          <Route path="/signin/challenge/recovery/" element={<PasswordRecovery />} />
          <Route path="/signup/" element={<SignUp />} />
          <Route path="*" element={<PageNotFound />} />
          <Route path="/myaccount/"
            element={
              <PrivateRoute>
                <Account />
              </PrivateRoute>
            }
          />
          <Route path="/myaccount/personalinfo/"
            element={
              <PrivateRoute>
                <PersonalInfo />
              </PrivateRoute>
            }
          />
          <Route path="/myaccount/name/"
            element={
              <PrivateRoute>
                <Name />
              </PrivateRoute>
            }
          />
        </Routes>
      </Router>
      </UserContextProvider>

    </ThemeProvider>
    </>
  )
}
